import {Component, ViewEncapsulation, Inject, Input} from '@angular/core';
import {DomSanitizer} from '@angular/platform-browser';
import { IUser } from '../models';

export const currentUser: IUser = {
  name: 'Yong',
  pictureDataUri: 'data:image/svg+xml;base64,'
};
@Component({
  selector: 'ngc-user-area',
  host: {
    class: 'user-area'
  },
  moduleId: module.id,
  templateUrl: 'user-area.html',
  styleUrls: ['user-area.css'],
  encapsulation: ViewEncapsulation.None
})
export class UserArea {
  @Input() openTasksCount:string;
  currentUser: IUser;
  sanitize(url:string){
    return this.ds.bypassSecurityTrustUrl(url);
  }
  constructor( private ds: DomSanitizer) {
    this.currentUser = currentUser;
  }
}